function Minicart(options) {
    this.formKey = options.formKey;
    this.previousVal = null;

    this.defaultErrorMessage = 'Error occurred. Try to refresh page.';

    this.selectors = {
        itemRemove:           '#cart-sidebar .remove',
        container:            '#header-cart',
        inputQty:             '.cart-item-quantity',
        qty:                  'div.header-minicart span.count',
        overlay:              '.minicart-wrapper',
        error:                '#minicart-error-message',
        success:              '#minicart-success-message',
        quantityButtonPrefix: '#qbutton-',
        quantityInputPrefix:  '#qinput-',
        quantityButtonClass:  '.quantity-button'
    };

    if (options.selectors) {
        $j.extend(this.selectors, options.selectors);
    }
}

Minicart.prototype = {
    initAfterEvents : {},
    removeItemAfterEvents : {},
    init: function() {
        var cart = this;

        // bind remove event
        $j(this.selectors.itemRemove).unbind('click.minicart').bind('click.minicart', function(e) {
            e.preventDefault();
            cart.removeItem($j(this));
        });

        // bind update qty event
        $j(this.selectors.inputQty)
            .unbind('blur.minicart')
            .unbind('focus.minicart')
            .bind('focus.minicart', function() {
                cart.previousVal = $j(this).val();
                cart.displayQuantityButton($j(this))
            })
            .bind('blur.minicart', function() {
                cart.revertInvalidValue(this);
            });

        $j(this.selectors.quantityButtonClass)
            .unbind('click.quantity')
            .bind('click.quantity', function() {
                cart.processUpdateQuantity(this);
        });

        for (var i in this.initAfterEvents) {
            if (this.initAfterEvents.hasOwnProperty(i) && typeof(this.initAfterEvents[i]) === "function") {
                this.initAfterEvents[i]();
            }
        }

    },

    removeItem: function(el) {
        var cart = this;
        if (confirm(el.data('confirm'))) {
            cart.hideMessage();
            cart.showOverlay();
            $j.ajax({
                type: 'POST',
                dataType: 'json',
                data: {form_key: cart.formKey},
                url: el.attr('href')
            }).done(function(result) {
                cart.hideOverlay();
                if (result.success) {
                    cart.updateCartQty(result.qty);
                    cart.updateContentOnRemove(result, el.closest('li'));
                } else {
                    cart.showMessage(result);
                }
            }).error(function() {
                cart.hideOverlay();
                cart.showError(cart.defaultErrorMessage);
            });
        }
        for (var i in this.removeItemAfterEvents) {
            if (this.removeItemAfterEvents.hasOwnProperty(i) && typeof(this.removeItemAfterEvents[i]) === "function") {
                this.removeItemAfterEvents[i]();
            }
        }
    },

    revertInvalidValue: function(el) {
        if (!this.isValidQty($j(el).val()) || $j(el).val() == this.previousVal) {
            $j(el).val(this.previousVal);
            this.hideQuantityButton(el);
        }
    },

    displayQuantityButton: function(el) {
        var buttonId = this.selectors.quantityButtonPrefix + $j(el).data('item-id');
        $j(buttonId).addClass('visible').attr('disabled',null);
    },

    hideQuantityButton: function(el) {
        var buttonId = this.selectors.quantityButtonPrefix + $j(el).data('item-id');
        $j(buttonId).removeClass('visible').attr('disabled','disabled');
    },

    processUpdateQuantity: function(el) {
        var input = $j(this.selectors.quantityInputPrefix + $j(el).data('item-id'));
        if (this.isValidQty(input.val()) && input.val() != this.previousVal) {
            this.updateItem(el);
        } else {
            this.revertInvalidValue(input);
        }
    },

    updateItem: function(el) {
        var cart = this;
        var input = $j(this.selectors.quantityInputPrefix + $j(el).data('item-id'));
        var quantity = parseInt(input.val(), 10);
        cart.hideMessage();
        cart.showOverlay();
        $j.ajax({
            type: 'POST',
            dataType: 'json',
            url: input.data('link'),
            data: {qty: quantity, form_key: cart.formKey}
        }).done(function(result) {
            cart.hideOverlay();
            if (result.success) {
                cart.updateCartQty(result.qty);
                if (quantity !== 0) {
                    cart.updateContentOnUpdate(result);
                } else {
                    cart.updateContentOnRemove(result, input.closest('li'));
                }
            } else {
                cart.showMessage(result);
            }
        }).error(function() {
            cart.hideOverlay();
            cart.showError(cart.defaultErrorMessage);
        });
        return false;
    },

    updateContentOnRemove: function(result, el) {
        var cart = this;
        el.hide('slow', function() {
            $j(cart.selectors.container).html(result.content);
            cart.showMessage(result);
            $j(".minicart-header-price").html(($j(".subtotal .price").html()));
        });
    },

    updateContentOnUpdate: function(result) {
        $j(this.selectors.container).html(result.content);
        this.showMessage(result);
    },

    updateCartQty: function(qty) {
        if (typeof qty != 'undefined') {
            $j(this.selectors.qty).text(qty);
        }
    },

    isValidQty: function(val) {
        return (val.length > 0) && (val - 0 == val) && (val - 0 > 0);
    },

    showOverlay: function() {
        $j(this.selectors.overlay).addClass('loading');
    },

    hideOverlay: function() {
        $j(this.selectors.overlay).removeClass('loading');
    },

    showMessage: function(result) {
        if (typeof result.notice != 'undefined') {
            this.showError(result.notice);
        } else if (typeof result.error != 'undefined') {
            this.showError(result.error);
        } else if (typeof result.message != 'undefined') {
            this.showSuccess(result.message);
        }
    },

    hideMessage: function() {
        $j(this.selectors.error).fadeOut('slow');
        $j(this.selectors.success).fadeOut('slow');
    },

    showError: function(message) {
        $j(this.selectors.error).text(message).fadeIn('slow');
    },

    showSuccess: function(message) {
        $j(this.selectors.success).text(message).fadeIn('slow');
    }
};

$j(document).ready(function () {
    $j("#header-mobile .categories-main").click(function () {
        $j(".modal-fade").toggle();
        $j('#header-mobile .categories').toggleClass('menu-active');
        $j("#header-mobile  #menu").stop().slideToggle(700);
        $j('#header-mobile .menubanner div').each(function () {
            bannersPosition($j(this));
        });
    });

    $j("#header .categories-main").click(function () {
        $j(".modal-fade").toggle();
        $j('#header .categories').toggleClass('menu-active');
        $j("#header  #menu").stop().slideToggle(700);
        $j('#header .menubanner div').each(function () {
            bannersPosition($j(this));
        });
    });

    $j(".modal-fade").click(function (c) {
        c.stopPropagation();
        $j(".modal-fade").addClass('fade-out');
        if ($j('.categories').hasClass('menu-active')) {
            $j(".categories").removeClass('menu-active');
        }
        $j(".close-on-fade-click").slideUp(500);
        setTimeout(function () {
            $j(".modal-fade").toggle();
            $j(".modal-fade").removeClass('fade-out');
        }, 500);
    });

    $j("#menu .nav-strefa-marek > a").click(function(e){
        e.preventDefault();
        if($j(this).next("ul").css("display") == "none"){
            $j(this).next("ul").slideDown(1000);
            $j(this).removeClass("closed");
        } else {
            $j(this).next("ul").slideUp(1000);
            $j(this).addClass("closed");
        }
    });

    $j(".footer .footer-col .block-title").click(function(){
        if($j(window).innerWidth() < 621) {
            console.log('mobile');
            if (!($j(this).hasClass('closed'))) {
                $j(this).next("ul").slideDown(1000);
                $j(this).addClass("closed");

            } else {
                $j(this).next("ul").slideUp(1000);
                $j(this).removeClass("closed");

            }
        }
    });
});

$j(document).load(function () {
    $j('.menubanner div').each(function () {
        bannersPosition($j(this));
    });
});


function bannersPosition(_this) {
    $j('li.level0').addClass('hovered');
    var bannerHeight = _this.height();
    bannerHeight -= 40;
    _this.closest('.menubanner').css('padding-bottom', bannerHeight);
    $j('li.level0').removeClass('hovered');


    var menuHeight = $j('#menu .menu-container').height();
    var itemHeight = _this.parent('li').parent('ul').height();
    itemHeight += 70;
    console.log(itemHeight);
    if (menuHeight > itemHeight) {
        if (bannerHeight > menuHeight) {
            bannerHeight += 30;
            _this.parent('li').parent('ul').css('min-height', bannerHeight);
        } else {
            _this.parent('li').parent('ul').css('min-height', menuHeight);
        }
    }
}
jQuery(document).ready(function(){

    jQuery(".add-to-wishlist.async").click(function(e){
        e.preventDefault();

        jQuery("body").append("<div id='loader-mask'><div id='spinner'></div></div>");
        var url = jQuery(this).attr("href");
        console.log(url);

        jQuery.ajax({
            url : url,
            type: "POST",
            cache: false,
            async: true,
            dataType: 'html'
        })
        .done(function(response) {
            changeWishlistNumber(response);
            displayMessage();
        });
    });

    function changeWishlistNumber(response)
    {
        var wrapperCut = response.substring(response.lastIndexOf('<div class="wishlist-link hidden-s4">'));
        var spanCut = wrapperCut.substring(wrapperCut.indexOf('<span>')+6);
        var spanCutEnd = spanCut.substring(0,spanCut.indexOf('</span>'));
        var wishlistNumber = parseInt(spanCutEnd);

        jQuery(".wishlist-link span").text(wishlistNumber);
    }

    function displayMessage()
    {
        jQuery("#loader-mask").html("<div id='message_box'>Dodano przedmiot do schowka</div>");
        setTimeout(function(){
            jQuery("#loader-mask").animate({
                opacity: 0,
              }, 1200, function() {
                jQuery("#loader-mask").remove();
              });

        }, 2000);
    }
});
