$j(document).ready(function(){
    // $j("#search").keyup(function(){
    //     console.log('test');
    //     debounce(function() {
    //         sendSolrRequest();
    //     }, 5);
    // });

    var searchFunction = debounce(function() {
        sendSolrRequest();
      }, 300);
    var search = document.getElementById("search");
    search.addEventListener('keyup', searchFunction);


    $j("#search_category").change(function(){
        sendSolrRequest();
    });
});

function debounce(func, wait, immediate) {
    var timeout;
    return function() {
        var context = this, args = arguments;
        var later = function() {
            timeout = null;
            if (!immediate) func.apply(context, args);
        };
        var callNow = immediate && !timeout;
        clearTimeout(timeout);
        timeout = setTimeout(later, wait);
        if (callNow) func.apply(context, args);
    };
};

function sendSolrRequest() {
    var query = $j("#search").val();
    var category = $j("#search_category").val();
    if(query.length > 2) {
        var request = $j.ajax({
        url: '/solr/request/query/',
        data: "q="+query+"&qcat="+category,
        error: function() {

        },
        success: function(response) {
           $j("#SolrSearchList").html(response);
        },
        type: 'POST'
     });
    }
    else {
        $j("#SolrSearchList").html("");
    }
}

