$j(document).ready(function () {

    $j('.product-description-section #tabs-nav li:first-child').addClass('active');
    $j('.product-description-section .tab-content').hide();
    $j('.product-description-section .tab-content:first').show();

    // Click function
    $j('.product-description-section #tabs-nav li').click(function(){
      $j('.product-description-section #tabs-nav li').removeClass('active');
      $j(this).addClass('active');
      $j('.product-description-section .tab-content').hide();
      
      var activeTab = $j(this).find('a').attr('href');
      $j(activeTab).fadeIn();
      return false;
    });

    $j(".product-image-thumbs").slick({
        slidesToShow: 5,
        slidesToScroll: 1,
        nextArrow: '<div class="nextArrow"></div>',
        prevArrow: '<div class="prevArrow"></div>',
    });
    $j('.product-description-section .slider-elements').slick({
        dots: true,
        infinite: false,
        prevArrow:"<img style='transform:rotate(180deg)' class='a-left control-c prev slick-prev' src='/files/arrow.png'>",
        nextArrow:"<img class='a-right control-c next slick-next' src='/files/arrow.png'>"
    });

    timeCounter = $j(".timer_box").data("time");
    timerInterval = setInterval(function () {
        timerTickTock();
    }, 1000);

    $j(".rating-star").click(function () {
        var starsCount = $j(this).index();
        var starsParent = $j(this).parent();

        starsParent.children('.rating-star').removeClass("active");

        for (var i = 0; i <= starsCount; i++) {
            starsParent.children('.rating-star').eq(i).addClass("active");
        }
    });

    $j(".go_up").click(function () {
        $j('html, body').animate({scrollTop: 0}, 1000, function () {

        });
    });

    $j(".product-menu-button").click(function () {
        var targetMenuId = $j(this).data("button");
        var targetDistanceTop = $j("#product-menu-" + targetMenuId).offset().top;

        $j('html, body').animate({scrollTop: targetDistanceTop}, 1000, function () {

        });
    });

    stickers();
    pickupAvailability();

    var urlAnchor = window.location.hash.substr(1);
    if (urlAnchor == 'scroll-to-askabout') {
        $j('html, body').animate({
            scrollTop: $j("#product-menu-askabout").offset().top
        }, 1000);
    }

});

function timerTickTock() {
    if (timeCounter > 0) {
        timeCounter = timeCounter - 1;

        var hours = Math.floor(timeCounter / 3600);
        var minutes = Math.floor(timeCounter / 60) % 60;
        var seconds = timeCounter % 60;

        hotshotItem = $j(".timer_box");
        if (hotshotItem.hasClass("hotshot_timer")) {
            if (hours < 10) {
                hours = "0" + hours;
            }
            if (hours >= 100) {
                hours = "<span class='plus'>+</span>" + 99;
            }
            if (minutes < 10) {
                minutes = "0" + minutes;
            }
            if (seconds < 10) {
                seconds = "0" + seconds;
            }
        }

        hotshotItem.find(".timer_seconds span").html(seconds);
        hotshotItem.find(".timer_minutes span").html(minutes);
        hotshotItem.find(".timer_hours span").html(hours);
    }
    else {
        clearInterval(timerInterval);
    }
}

function stickers() {
    $j('.stickers').each(function () {
        var _this = $j(this);
        var stickerLenght = _this.children('div').length;
        if (stickerLenght > 2) {
            _this.slick({
                arrows: false,
                dots: false,
                infinite: false,
                autoplay: true,
                autoplaySpeed: 4000
            });
        }
    })
};

function pickupAvailability() {
    $j('#pickup-availability').click(function (e) {
        e.preventDefault();
        e.stopPropagation();

        $j('#pickup-popup').remove();

        var pickupUrl = $j(this).data('pickup-url');
        $j.ajax({
            url: pickupUrl,
            success: function (data) {
                pickupAvailabilityData(data);
            }
        });
    });

    $j(document).on('change', '#pickup-filter', function () {
        var selectedCity = $(this).value;
        $j('#pickup-popup .pickup-store').each(function () {
            var pickupCity = $j(this).data('city');
            if (selectedCity == 'all') {
                $j(this).show();
            } else {
                if (!(pickupCity == selectedCity)) {
                    $j(this).hide();
                } else {
                    $j(this).show();
                }
            }
        })
    });
}

function pickupAvailabilityData(data) {
    $j('.pickup-availability').append(data);

    $j('html, body').animate({scrollTop: 0}, 1000);
    $j('.pickup-availability #pickup-popup').stop().slideDown(700);
    $j(".modal-fade").show();

    setTimeout(function () {
        fixedHeader();
    }, 1001);
}

function fixedHeader() {
    $j('#pickup-popup .popup_header').removeClass('fixed');
    var headerTopPositionOrig = $j('#pickup-popup .popup_header').offset().top;
    var headerTopPosition;
    var footerBottomPosition = (headerTopPositionOrig + $j('#pickup-popup').height()) - $j('#pickup-popup .popup_header').height() - 40;

    setTimeout(function () {
        if ($j('#pickup-popup .popup_header').offset().top - $j(window).scrollTop() <= 0 && $j(window).scrollTop() > 0 && $j('#pickup-popup .popup_header').offset().top < footerBottomPosition) {
            $j('#pickup-popup .popup_header').addClass('fixed');
        }
    }, 0);

    $j(window).on('scroll', function () {
        setTimeout(function () {
            if ($j(window).scrollTop() <= headerTopPositionOrig || $j(window).scrollTop() > footerBottomPosition) {
                $j('#pickup-popup .popup_header').removeClass('fixed');
                $j('#pickup-popup .popup_content').css({'padding-top': 0});
            } else {
                headerTopPosition = $j('#pickup-popup .popup_header').offset().top - $j(window).scrollTop();
                if (headerTopPosition <= 0) {
                    $j('#pickup-popup .popup_header').addClass('fixed');
                    $j('#pickup-popup .popup_content').css({'padding-top': $j('#pickup-popup .popup_header').outerHeight()});
                }
            }
        }, 0);
    });
}



$j(document).ready(function(){

    $j("#review-form").submit(function(e){
        e.preventDefault();
        sendReviewRequest($j(this), $j(this).attr("action"));
    });

    $j("#more_reviews_button").click(function(){
        $j("#customer-reviews").addClass("active");
    });
});

function sendReviewRequest(array, requestUrl)
{
    var formToValidate = $('review-form');
    var validator = new Validation(formToValidate);
    if ( validator.validate() ) {
        $j(".review-add").html("<div class='spinner'></div>");
        var serializedData = array.serialize();
        var request = $j.ajax({
            url: requestUrl,
            data: serializedData,
            error: function() {
                $j(".review-add").html("<h2 class='review-title'>Przepraszamy. Wystpi bd</h2>");
            },
            success: function(response) {
                console.log(response);
                $j(".review-add").html("<h2 class='review-title'>Twoja opinia trafia do moderacji. Dzikujemy!</h2>");
            },
            type: 'POST'
        });
    }
    else{
       
    }
}
